// ====== 動態陣列，用來儲存符號表等的基本結構嗎？=======void dynarray_add(void ***ptab, int *nb_ptr, void *data){    int nb, nb_alloc;    void **pp;        nb = *nb_ptr;    pp = *ptab;    // every power of two we double array size    if ((nb & (nb - 1)) == 0) {        if (!nb)            nb_alloc = 1;        else            nb_alloc = nb * 2;        pp = tcc_realloc(pp, nb_alloc * sizeof(void *));        if (!pp)            error("memory full");        *ptab = pp;    }    pp[nb++] = data;    *nb_ptr = nb;}void dynarray_reset(void *pp, int *n){    void **p;    for (p = *(void***)pp; *n; ++p, --*n)        if (*p)            tcc_free(*p);    tcc_free(*(void**)pp);    *(void**)pp = NULL;}